mapper.select:
select count(1) from au_user


        and loginCode=#{loginCode}


        and id!=#{id}

mapper.update:
update au_user
loginCode = #{loginCode}, userName = #{userName}, password = #{password}, password2 = #{password2}, sex = #{sex}, birthday = #{birthday}, cardType = #{cardType}, cardTypeName = #{cardTypeName}, idCard = #{idCard}, country = #{country}, mobile = #{mobile}, email = #{email}, userAddress = #{userAddress}, postCode = #{postCode}, createTime = #{createTime}, referId = #{referId}, referCode = #{referCode}, roleId = #{roleId}, roleName = #{roleName}, userType = #{userType}, userTypeName = #{userTypeName}, isStart = #{isStart}, lastUpdateTime = #{lastUpdateTime}, lastLoginTime = #{lastLoginTime}, bankName = #{bankName}, accountHolder = #{accountHolder}, bankAccount = #{bankAccount}, idCardPicPath = #{idCardPicPath}, bankPicPath = #{bankPicPath}
where id=#{id}

mapper.select:
select count(1) from au_user

    and loginCode like concat("%",#{loginCode},"%")
    and userName like concat("%",#{userName},"%")
    and referCode like concat("%",#{referCode},"%")
    and roleId=#{roleId}
    and isStart=#{isStart}

select * from au_user

    and loginCode like concat("%",#{loginCode},"%")
    and userName like concat("%",#{userName},"%")
    and referCode like concat("%",#{referCode},"%")
    and roleId=#{roleId}
    and isStart=#{isStart}

order by createTime desc limit #{starNum},#{pageSize}
